<?php
class Product {
    private $title;
    private $image;
    private $price;

    public function __construct($title, $image, $price) {
        $this->title = $title;
        $this->image = $image;
        $this->price = $price;
    }

    public function getTitle() {
        return $this->title;
    }

    public function getImage() {
        return $this->image;
    }

    public function getPrice() {
        return $this->price;
    }
}

class Store {
    private $name;
    private $image;
    private $description;
    private $products = [];

    public function __construct($name, $image, $description) {
        $this->name = $name;
        $this->image = $image;
        $this->description = $description;
    }

    public function addProduct(Product $product) {
        $this->products[] = $product;
    }

    public function getName() {
        return $this->name;
    }

    public function getImage() {
        return $this->image;
    }

    public function getDescription() {
        return $this->description;
    }

    public function getProducts() {
        return $this->products;
    }
}
?>

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Online Store</title>
    <style>
        /* Add your CSS styling here */
    </style>
</head>
<body bgcolor="gray">
<center>
<?php
    // Create store and products
    $store = new Store("My Online Store", "https://th.bing.com/th/id/OIP.QpvaIo8zEht_DuyG_yDQtQHaEV?w=321&h=187&c=7&r=0&o=5&dpr=1.1&pid=1.7", "Welcome to my online store!");
    $store->addProduct(new Product("Product 1", "data:image/jpeg;base64,", 100));
    $store->addProduct(new Product("Product 2", "https://th.bing.com/th/id/OIP.dix7Fd8zqanp9laNBNAFuQHaFj?w=200&h=180&c=7&r=0&o=5&dpr=1.1&pid=1.7", 150));
    $store->addProduct(new Product("Product 3", "data:image/jpeg;base64,", 200));
    ?>

    <div class="store">
        <h1><?php echo $store->getName(); ?></h1>
        <img src=" <?php echo $store->getImage(); ?>" alt="Store Image">
        <p><?php echo $store->getDescription(); ?></p>
    </div>

    <div class="products">
        <h2>Products</h2>
        <?php foreach ($store->getProducts() as $product): ?>
            <div class="product">
                <h3><?php echo $product->getTitle(); ?></h3>
                <img src=" <?php echo $product->getImage(); ?>" alt="Product Image">
                <p>Price: $<?php echo $product->getPrice(); ?></p>
            </div>
        <?php endforeach; ?>
    </div>
</center>
</body>
</html>
